	var record = {
		"questions": [{"choices": [ { "choice": "Audi" }, { "choice": "BMW" }, {"choice": "Corvette" }, {"choice": "Tesla"}],"question": "what is your favorite car"}],
		"usergroup": "Software", 
		"name": "Client posted poll"
	};
	$http.post(currApi, record)
		.success(function (data) {
				console.log("LAB 3 POST");
